import React from 'react'
import { motion } from 'framer-motion';
import "../../App.css"

const AnimatedImage = ({}) => {

    const child = {
        visible: {
            opacity: 1,
            x: 0,
            transition: {
                type: "spring",
                damping: 18,
                stiffness: 100,
            },
        },
        hidden: {
            opacity: 0,
            x: 20,
            transition: {
                type: "spring",
                damping: 18,
                stiffness: 100,
            },
        },
    };

    return (
        <motion.img
            style={{ overflow: "hidden", display: "flex"}}
            style={{ width: "390px", height: "700px" }}
            initial="hidden"
            whileInView="visible"
        >
        </motion.img>
    )
}
export default AnimatedImage;
